import React, { useState, useEffect } from "react";
import { getRooms } from "../helpers/database";

function Room() {

  const [rooms, setRooms] = useState("");

  useEffect(() => {
    getRoomList();
  }, []);

  let itemId = 1;
  const createItem = (text) => {
    return (itemId++)+text;
  }

  const getRoomList = () => {
    const roomList = getRooms();
    console.log(roomList);
    const myList = () => {
      const list = roomList.map((room, index) => (
        <li key={createItem(index+"li")}>
          <div key={createItem(index+"room")} className = "room"><span>{room}</span></div>
        </li>
      ));
      return <ul>{list}</ul>;
    };
    setRooms(myList);
  };

  return (
    <div className="room-wrap">
      <div>
        {rooms}
      </div>
    </div>
  );
}

export default Room;
