import { database, database_set, database_ref } from "../services/firebase";

export function sendChat(data) {
  // return database_set(database_ref(database, 'chats'), {
  //   message: data.message,
  //   timestamp: data.timestamp,
  //   uid: data.uid,
  // });

  return database_ref("chats/"+data.uid).push({
    uid: data.uid,
    message: data.message,
    timestamp: data.timestamp,
    userName: data.userName
  });
}
