import { database } from "../services/firebase";

export function sendChat(data) {
  return set(ref(database, 'chats'), {
    message: data.message,
    timestamp: data.timestamp,
    uid: data.uid,
  });

  return database.ref("chats").push({
    message: data.message,
    timestamp: data.timestamp,
    uid: data.uid,
  });
}
