import { database, database_set, database_ref, onValue } from "../services/firebase";

export function sendChat(data) {
  return database_set(database_ref(database, 'chats/sesh/'+data.timestamp), {
    message: data.message,
    timestamp: data.timestamp,
    email: data.email,
    uid: data.uid
  });
}

export function getChats() {
  let chats = [];
  database.ref("chats/sesh").on("value", (snapshot) => {
    snapshot.forEach((row) => {
      chats.push(row.val());
    });
  });
  return chats;
}
