import { database, database_set, database_ref, database_on_value } from "../services/firebase";
import { onValue, onChildAdded, onChildChanged, onChildRemoved } from "firebase/database";

export function sendChat(data) {
  return database_set(database_ref(database, 'chats/sesh/'+data.timestamp), {
    message: data.message,
    timestamp: data.timestamp,
    email: data.email,
    uid: data.uid
  });
}

export function getChats() {
  let chats = [];
  const chatRef = database_ref(database, "chats/sesh");
  onValue(chatRef, (snapshot) => {
    snapshot.forEach((row) => {
      chats.push(row.val());
    });
  });
  return chats;
}
