import React, { useState, useEffect, useRef } from "react";
import { logout } from "../helpers/auth";
import { authService, database, database_ref } from "../services/firebase";
import { sendChat, getChats, chatOnValue, chatOnChildAdded, chatOnChildChanged, chatOnChildRemoved } from "../helpers/database";

function Chat() {

  const [msg, setMsg] = useState("");
  const [chats, setChats] = useState("");

  const messagesEndRef = useRef(null);
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }

  const getChatList = () => {
    const chatList = getChats();
    console.log(chatList);
    const listItem = chatList.map((chat) =>
      <li>{chat.message}</li>
    );
    //setChats(chatList);
  };

  const handleOnChange = (e) => {
  	setMsg(e.target.value);
  };

  const handleSumbit = async (e) => {
    e.preventDefault();
    console.log(msg);
    try {
      await sendChat({
        uid: authService.currentUser.uid,
        email: authService.currentUser.email,
        message: msg,
        timestamp: Date.now()
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleGoogleLogOut = async () => {
    try {
      await logout();
    } catch (error) {
     console.log(error);
    }
  };

  useEffect(() => {
    try {
      //chatOnChildAdded
      const commentsRef = database_ref(database, 'chats/sesh');
      chatOnChildChanged(commentsRef, (data) => {
        console.log("chatOnChildChanged()");
        getChatList();
      });
      console.log("useEffect getChatList()");
      getChatList();
      scrollToBottom();
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <div className="chat-container">
      <div className="chat-top">헤더</div>
      <div className="chat-middle">
        <li className="chat-bubble send">
          <p>

          </p>
          <span>13:30PM</span>
        </li>
        <li className="chat-bubble receive">
          <p>방가방가!!</p>
          <span>13:31PM</span>
        </li>
      </div>
      <div className="chat-bottom">
        <form onSubmit={handleSumbit}>
          <input
            placeholder="내용을 입력하세요."
            value={msg}
            onChange={handleOnChange}
          />
          <button type="submit">전송</button>
        </form>
      </div>

      <button
        type="button"
        onClick={handleGoogleLogOut}>
        로그아웃
      </button>

    </div>

  );
}

export default Chat;
