import React, { useState, useEffect, useRef } from "react";
import { logout } from "../helpers/auth";
import { authService, database, database_ref } from "../services/firebase";
import { sendChat, getChats, chatOnValue, chatOnChildAdded, chatOnChildChanged, chatOnChildRemoved } from "../helpers/database";
import "../chat.css";

function Chat() {

  const [msg, setMsg] = useState("");
  const [chats, setChats] = useState("");

  const messagesEndRef = useRef(null);
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }

  let itemId = 1;
  const createItem = (text) => {
    return (itemId++)+text;
  }

  const toDate = (timestamp) => {
    let date = new Intl.DateTimeFormat('ko-KR', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(timestamp);
    return date;
  }
  const getChatList = () => {
    const chatList = getChats();
    const myList = () => {
      const list = chatList.map((chat, index) => (
        // <div style={{width:"100%"}} key={index} className={chat.uid === authService.currentUser.uid ? "send" : "receive"}>{chat.message}</div>
        <li key={createItem(index+"li")} className={chat.uid === authService.currentUser.uid ? "right" : "left"}>
          <div key={createItem(index+"sender")} className="sender"><span>{chat.email}</span></div>
          <div key={createItem(index+"msg")} className={[chat.uid === authService.currentUser.uid ? "send" : "receive", "message"].join(" ")}><span>{chat.message}</span></div>
          <div key={createItem(index+"time")} className={[chat.uid === authService.currentUser.uid ? "send" : "receive", "time"].join(" ")}>{toDate(chat.timestamp)}</div>
        </li>
      ));
      return <ul>{list}</ul>;
    };
    setChats(myList);
  };

  const handleOnChange = (e) => {
  	setMsg(e.target.value);
  };

  const handleSumbit = async (e) => {
    e.preventDefault();
    //console.log(msg);
    try {
      await sendChat({
        uid: authService.currentUser.uid,
        email: authService.currentUser.email,
        message: msg,
        timestamp: Date.now()
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleGoogleLogOut = async () => {
    try {
      await logout();
    } catch (error) {
     console.log(error);
    }
  };

  useEffect(() => {
    try {
      //chatOnChildChanged
      const commentsRef = database_ref(database, 'chats/sesh');
      chatOnChildAdded(commentsRef, (data) => {
        getChatList();
      });
      scrollToBottom();
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <div className="chat_wrap chat-container">
      <div className="chat-top header">
        <button
          className="logout"
          type="button"
          onClick={handleGoogleLogOut}>
          로그아웃
        </button>
      </div>
      <div className="chat chat-middle">
        {chats}
      </div>
      <form onSubmit={handleSumbit}>
        <div className="input-div">
          <textarea
            className="input-msg"
            placeholder="Press Enter for send message."
            value={msg}
            onChange={handleOnChange}>
          </textarea>
          <button className="submit" type="submit">전송</button>
        </div>
      </form>

      <div className="chat-bottom">
      </div>
    </div>

  );
}

export default Chat;
