import React, { useState, useEffect, useRef } from "react";
import { logout } from "../helpers/auth";
import { authService, database, database_ref } from "../services/firebase";
import { sendChat, getChats, chatOnValue, chatOnChildAdded, chatOnChildChanged, chatOnChildRemoved } from "../helpers/database";
import "../chat.css";

function Chat() {

  const [msg, setMsg] = useState("");
  const [chats, setChats] = useState("");

  const messagesEndRef = useRef(null);
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }

  let itemId = 1;
  const createItem = (text) => {
    return (itemId++)+text;
  }

  const toDate = (timestamp) => {
    let date = new Intl.DateTimeFormat('ko-KR', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(timestamp);
    return date;
  }
  const getChatList = () => {
    const chatList = getChats();
    const myList = () => {
      const list = chatList.map((chat, index) => (
        // <div style={{width:"100%"}} key={index} className={chat.uid === authService.currentUser.uid ? "send" : "receive"}>{chat.message}</div>
        <div>
          <div key={createItem(index+"msg")} className={chat.uid === authService.currentUser.uid ? "send" : "receive"}>{chat.message}</div>
          <div key={createItem(index+"time")} class={[chat.uid === authService.currentUser.uid ? "send" : "receive", "time"].join(" ")}>{toDate(chat.timestamp)}</div>
        </div>
      ));
      return <div>{list}</div>;
    };
    setChats(myList);
  };

  const handleOnChange = (e) => {
  	setMsg(e.target.value);
  };

  const handleSumbit = async (e) => {
    e.preventDefault();
    //console.log(msg);
    try {
      await sendChat({
        uid: authService.currentUser.uid,
        email: authService.currentUser.email,
        message: msg,
        timestamp: Date.now()
      });
    } catch (error) {
      console.log(error);
    }
  };

  const handleGoogleLogOut = async () => {
    try {
      await logout();
    } catch (error) {
     console.log(error);
    }
  };

  useEffect(() => {
    try {
      //chatOnChildChanged
      const commentsRef = database_ref(database, 'chats/sesh');
      chatOnChildAdded(commentsRef, (data) => {
        getChatList();
      });
      scrollToBottom();
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <div className="chat-container">
      <div className="chat-top">
      </div>
      <div className="chat-middle">
        {chats}
      </div>
      <div className="chat-bottom">
        <form onSubmit={handleSumbit}>
          <input
            className="input-msg"
            placeholder="내용을 입력하세요."
            value={msg}
            onChange={handleOnChange}
          />
          <button className="submit" type="submit">전송</button>
        </form>
      </div>

      <button
        type="button"
        onClick={handleGoogleLogOut}>
        로그아웃
      </button>

    </div>

  );
}

export default Chat;
