import { database, database_set, database_ref, database_on_value } from "../services/firebase";
import { onValue, onChildAdded, onChildChanged, onChildRemoved, query, orderByKey, orderByChild, limitToLast, startAt } from "firebase/database";

export function getRooms(callback) {
  const roomRef = database_ref(database, "chats");
  onValue(roomRef, (snapshot) => {
    callback(snapshot.val());
  });
}

export function sendChat(roomName, data) {
  return database_set(database_ref(database, 'chats/'+roomName+"/"+data.timestamp), {
    message: data.message,
    timestamp: data.timestamp,
    email: data.email,
    uid: data.uid
  });
}

export function getChats(roomName, callback) {
  console.log("database getChats()")
  let chatList = [];
  const date = new Date();
  const today = (new Date(date.getFullYear(), date.getMonth(), date.getDate(), 0, 0, 0, 0)).getTime();
  // console.log(today);
  const chatRef = query(database_ref(database, 'chats/'+roomName), orderByKey(), startAt(today.toString()), limitToLast(2));
  // const chatRef = database_ref(database, "chats/"+roomName);
  onValue(chatRef, (snapshot) => {
    snapshot.forEach((row) => {
      chatList.push(row.val());
    });
    console.log("getChats")
    console.log(chatList)
    callback(chatList);
  });
}

export function getAddedChats(roomName, callback) {
  let chatList = [];
  const chatRef = database_ref(database, 'chats/'+roomName);
  onChildAdded(chatRef, (snapshot) => {
    snapshot.forEach((row) => {
      chatList.push(row.val());
    });
    console.log("getAddedChats")
    console.log(chatList)
    // callback(chatList);
  });
}

export const chatOnValue = onValue;
export const chatOnChildAdded = onChildAdded;
export const chatOnChildChanged = onChildChanged;
export const chatOnChildRemoved = onChildRemoved;
