    //   const list = Object.keys(roomObject).map((roomName, index) => (
    //     <li key={createItem(index+"li")}>
    //       <button
    //         key={createItem(index+"room")}
    //         className = "room"
    //         type="button"
    //         onClick={handleSelectRoom}
    //         value={roomName}>
    //         <span>{roomName}</span>
    //       </button>
    //     </li>
    //   ));
    //   return <ul>{list}</ul>;
    // };
    //
    // console.log(myList)
    // setRooms(myList);
  };

  return (
    <div className="room-wrap">
      <button
        className="logout-room"
        type="button"
        onClick={handleGoogleLogOut}>
        LOGOUT
      </button>
      <div>
        {rooms}
      </div>
    </div>
  );
}

export default Room;
