import { authService, createUser } from "../services/firebase";

export function signUp(email, password) {
  return createUserEmail(authService, email, password);
}

export function signIn(email, password) {
  return signInEmail(email, password);
}

export function logout() {
  return authService.signOut();
}

export function signInWithGoogle() {
  const provider = new authService.GoogleAuthProvider();
  return authService.signInWithPopup(provider);
}
