import React, { useReducer, useEffect, useRef  } from 'react';
import { HashRouter, Routes, Route  } from 'react-router-dom';
import { authService } from "./services/firebase";
// import "./styles.css";
import Chat from "./pages/Chat";
import Room from "./pages/Room";
import SignUp from "./pages/SignUp";
import Login from "./pages/Login";
import PublicRoute from "./components/PublicRoute";
import PrivateRoute from "./components/PrivateRoute";

const initialState = {
  authenticated: false,
  loading: true
};

function reducer(state, action) {
  switch (action.type) {
    case "GET_USER":
      return { ...state, authenticated: action.result };
    default:
      return state;
  }
}


const usePushNotification = (title, options) => {
  const notificationRef = useRef(null);

  // Notification이 지원되지 않는 브라우저가 있을 수 있기 때문에, 이를 대비해 Early return 문을 걸어준다.
  if (!Notification) {
    return;
  }
  console.log("usePushNotification")
  console.log(title)

  const fireNotif = () => {
    /* 권한 요청 부분 */
    if (Notification.permission !== "granted") {
      Notification.requestPermission().then((permission) => {
        if (permission === "granted") {
          /* 권한을 요청받고 nofi를 생성해주는 부분 */
          new Notification(title, options);
        } else {
          return;
        }
      });
    } else {
      /* 권한이 있을때 바로 noti 생성해주는 부분 */
      new Notification(title, options);
    }
  };
  return fireNotif;
};

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { authenticated } = state;

  useEffect(() => {
    authService.onAuthStateChanged((user) => {
      if (user) {
        dispatch({
          type: "GET_USER",
          result: true,
        });
      } else {
        dispatch({
          type: "GET_USER",
          result: false,
        });
      }
    });
  }, []);

  return (
    <HashRouter>
      <Routes>

        <Route
          path="/"
          element={<PublicRoute authenticated={authenticated} component={Login} />}
        />

        <Route
          path="/signup"
          element={<PublicRoute authenticated={authenticated} component={SignUp} />}
        />

        <Route
          path="/login"
          element={<PublicRoute authenticated={authenticated} component={Login} />}
        />

        <Route
          path="/chat"
          element={<PrivateRoute authenticated={authenticated} component={Chat} />}
        />

        <Route
          path="/room"
          element={<PrivateRoute authenticated={authenticated} component={Room} />}
        />

      </Routes>
    </HashRouter>
  );
}

export default App;
export const useNotification = usePushNotification
