import { useNavigate } from "react-router-dom";

export function useNotification(title, option) {

  if (!("Notification" in window)) {
    return;
  }

  if (Notification.permission !== "granted") {
    try {
      Notification.requestPermission().then((permission) => {
        if (permission !== 'granted') return;
      });
    } catch (error) {
      if (error instanceof TypeError) {
        Notification.requestPermission((permission) => {
          if (permission !== 'granted') return;
        });
      } else {
        console.error(error);
      }
    }
  }else {
    const roomName = option.body.split("from '")[1];
    console.log(option.body);
    console.log(roomName);
    console.log(roomName.substring(0, roomName.length-1));
    var notification = new Notification(title, option);
    // self.addEventListener('notificationclick', function(event) {
    notification.onclick = function(event) {
      event.preventDefault();
      window.focus();
      const url = "http://localhost:3000/#/chat?room="+roomName;
      console.log(url);
      window.open(url, "_self");

      // navigate(`/chat?room=${title}`);
      // notificationRef.close();
    }
  }

};
